import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const ReportForm = () => {
    const [studentName, setStudentName] = useState('');
    const [studentId, setStudentId] = useState('');
    const [subject, setSubject] = useState('');
    const [grade, setGrade] = useState('');
    const [comments, setComments] = useState('');
    const [submitted, setSubmitted] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();
        const reportData = { studentName, studentId, subject, grade, comments };
        console.log('Report Data:', reportData);
        setStudentName('');
        setStudentId('');
        setSubject('');
        setGrade('');
        setComments('');
        setSubmitted(true);
        // Here you can add functionality to send reportData to a server or process it further
    };

    // Reset submitted state when any input changes
    const handleInputChange = (setter) => (e) => {
        setter(e.target.value);
        setSubmitted(false);
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label htmlFor="studentName">Student Name:</label>
                <input
                    id="studentName"
                    type="text"
                    value={studentName}
                    onChange={handleInputChange(setStudentName)}
                    required
                />
            </div>
            <div>
                <label htmlFor="studentId">Student ID:</label>
                <input
                    id="studentId"
                    type="text"
                    value={studentId}
                    onChange={handleInputChange(setStudentId)}
                    required
                />
            </div>
            <div>
                <label htmlFor="subject">Subject:</label>
                <input
                    id="subject"
                    type="text"
                    value={subject}
                    onChange={handleInputChange(setSubject)}
                    required
                />
            </div>
            <div>
                <label htmlFor="grade">Grade:</label>
                <input
                    id="grade"
                    type="text"
                    value={grade}
                    onChange={handleInputChange(setGrade)}
                    required
                />
            </div>
            <div>
                <label htmlFor="comments">Comments:</label>
                <textarea
                    id="comments"
                    value={comments}
                    onChange={handleInputChange(setComments)}
                />
            </div>
            <button type="submit">Generate Report</button>
            {submitted && <p style={{ color: 'green' }}>Report generated successfully!</p>}
            <div style={{ marginTop: '10px' }}>
                <Link to="/">Back to Home</Link>
            </div>
        </form>
    );
};

export default ReportForm;
